package secao4;

public class ex05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 7;
		
		if(num %2 == 0) {
			System.out.println("O número é par");
		}else {
			System.out.println("O número é ímpar");
		}
		
		System.out.println((num %2 == 0) ? "O número é par" : "O número é ímpar");
	}

}
