package secao4;

public class ex18 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int valor1 = 10, valor2 = 2;
		String operacao = "*";
		
		switch(operacao) {
		case "+":
			System.out.println((valor1 + valor2));
			break;
		case "*":
			System.out.println((valor1 * valor2));
			break;
		case "-":
			System.out.println((valor1 - valor2));
			break;
		case "/":
			System.out.println((valor1 / valor2));
			break;
		}
	}

}
