package secao5;

public class ex17 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numero = 10, soma = 0;
		
		for(int i = 0; i <= numero; i++) {
			soma += i;
		}
		
		System.out.println("Soma dos número inteiros positivos de 0 até " + numero + ": " + soma);
	}

}
